/* eslint-disable no-unused-vars */
import React, { useState } from 'react';
import axios from 'axios';
const Chatbox = () => {
  const [message, setMessage] = useState('');
  const [recievedMessage, setRecievedMessage] = useState('loading');

  const handleSubmit = async (e) => {
    e.preventDefault();
    //when submitting,the recieved message should be none
    //
    setRecievedMessage('');
    const res = await axios.post('http://localhost:3001/recieve', { message });
    setRecievedMessage([res.data]);
    setMessage('');
  };

  return (
    <div>
      <form action=''>
        <input
          placeholder='Type Here...'
          type='text'
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <button onClick={handleSubmit}>Submit</button>
      </form>
      <br />
      <br />
      <div>
        {recievedMessage.length > 7 ? (
          <p>{recievedMessage}</p>
        ) : (
          <h1>Loading...</h1>
        )}
      </div>
    </div>
  );
};

export default Chatbox;
